import Course from "./Course";
import Axios from 'axios';
import BASE_API_URL from "./api/Bootapi";
import { Toast } from "bootstrap";


const Allcourses =()=>{
useEffect(()=>{
    document.title="all course || learn with ps";
},[])

const getAllCoursesFromServer=()=>{
 Axios.get('http://localhost:1010/app/v1/courses').then(
     (Response)=>{
         console.log(Response.data);
        setCourses(Response.data);
     },
     (Error)=>{
         console.log(Error);
     }
     
 )
}

useEffect(()=>{
    getAllCoursesFromServer();
},[]);
    const [courses,setCourses] = useState([
        // {title:'java tutorials', description:'this is java tutorials for biginer'},
        // {title:'pythom tutorials', description:'this is python tutorials for biginer'},
        // {title:'angular tutorials', description:'this is angular tutorials for biginer'},
        // {title:'react tutorials', description:'this is react tutorials for biginer'}
        ])

        const updateCourses=(id)=>{
setCourses(courses.filter((c)=>c.id!=id));
        };
    return(
<div>
<h1>List of courses</h1>
{console.log("i wanna check lenthg of course:",courses.length)}
<p>please follow of these courses </p>
{
    courses.length>0 ? courses.map((item)=><Course course={item} update={updateCourses}/>):'no courses are available'
}
</div>
);
}
export default Allcourses;
