import { Fragment,useEffect, useState } from "react";
import { Button, Container, Form, FormGroup, Input, Label, Toast } from "reactstrap";
import axios from "axios";



const AddCourse =()=>{
    useEffect(()=>{
        document.title="Add course || with ps";
    },[])
const [course,setCourses] = useState({});

const handleForm=(e)=>{
    console.log(course);
    postDataToServer(course);
    e.preventDefault();
    
}

const postDataToServer=(data)=>{
axios.post('http://localhost:1010/app/v1/courses',data).then(
    (Response)=>{
        console.log(Response.data);

    },
    (Error)=>{
console.log(Error);
    }
)

}

return<Fragment>
    <Form onSubmit={handleForm}>
    <h1 className="text-center py-3">Please enter the detail of course</h1>
        <FormGroup>
            <Label for="courseId">Enter Cosere id</Label>
            <Input type="text" placeholder="enter course id" name="courseId" id="courseId"
            onChange={(e)=>
            setCourses({...course,id:e.target.value})
            }
            ></Input>
            
        </FormGroup>
        <FormGroup>
            <Label for ='title'>Enter course title</Label>
            <Input type ='text' placeholder="enter title " id="title"
            onChange={(e)=>
                setCourses({...course,title:e.target.value})
            }
            ></Input>
        </FormGroup>
        <FormGroup>
            <label for='description'>enter course description</label>
            <Input style={{height:'100'}} type="textarea" placeholder="write about course" id="description"
            onChange={(e)=>
            setCourses({...course,description:e.target.value})
            }
            />
        </FormGroup>
        <Container className="text-center">
            <Button type="submit" color="success">Add</Button>
            <Button type="reset" color="warning" style={{marginLeft:'10px'}}>Clear</Button>
        </Container>
    </Form>
</Fragment>
}
export default AddCourse;
