import axios from "axios";
import React from "react";
import { Button, Card, CardBody, CardText, CardTitle, Container } from "reactstrap";

const Course = ({course,update}) => {

const deleteCourse=(id)=>{
    
axios.delete('http://localhost:1010/app/v1/courses'+'/'+id).then(
    (Response)=>{
        console.log(Response);
        
        update(id);
    },
    (Error)=>{
        console.log(id);
        console.log(Error);
    }
)
}

    return <>
        <Card>
            <CardBody className="text-center">
                <CardTitle style={{fontSize:'large', fontStyle:'bold'}}>{course.title}</CardTitle>
                <CardText>{course.description}</CardText>
                <Container className="text-center">
                    <Button color='danger' onClick={()=>{
                        deleteCourse(course.id);
                    }}>Delete</Button>
                    <Button color='warning' style={{marginLeft:'10px'}}>Update</Button>
                </Container>
            </CardBody>
        </Card>
    </>
}
export default Course;
